mapper.insert:
insert into landmark (
    name, category, city, state, id, isFeatured, isFavorite, park, description, imageName
)
values (
    #{name}, #{category}, #{city}, #{state}, #{id}, #{isFeatured}, #{isFavorite}, #{park}, #{description}, #{imageName}
);

insert into coordinates (
    id, longitude, latitude
)
values (
    #{id}, truncate(#{coordinates.longitude}, 6), truncate(#{coordinates.latitude}, 6)
);

mapper.update:
update landmark set
    isFavorite = #{isFavorite},
    isFeatured = #{isFeatured}
where
    id = #{id}

mapper.insert:
insert into hike (
    name, id, distance, difficulty
)
values (
    #{name}, #{id}, truncate(#{distance}, 1), #{difficulty}
);


    insert into observations(
        id, seq, elevation1, elevation2,
        pace1, pace2, heartRate1, heartRate2,
        distanceFromStart
    )
    values
    (
        #{id}, #{observation.seq}, truncate(#{observation.elevation1}, 14), truncate(#{observation.elevation2}, 14),
        truncate(#{observation.pace1}, 14), truncate(#{observation.pace2}, 14),
        truncate(#{observation.heartRate1}, 14), truncate(#{observation.heartRate2}, 14),
        truncate(#{observation.distanceFromStart}, 3)
    );
